/**
 * @param type - "evm" or "solana", extend this union as needed (e.g., "sui")
 * @param id - Chain ID, optional for EVM
 */
type NativeCurrency = {
    name: string;
    symbol: string;
    decimals: number;
};
type Chain = EvmChain | SolanaChain | AptosChain | ChromiaChain | FuelChain | SuiChain | ZilliqaChain | CosmosChain | StarknetChain | RadixChain | ZetrixChain;
type SuiChain = {
    type: "sui";
};
type EvmChain = {
    type: "evm";
    id: number;
    nativeCurrency: NativeCurrency;
};
type SolanaChain = {
    type: "solana";
    nativeCurrency: NativeCurrency;
};
type AptosChain = {
    type: "aptos";
};
type ChromiaChain = {
    type: "chromia";
};
type FuelChain = {
    type: "fuel";
};
type ZilliqaChain = {
    type: "zilliqa";
    id: number;
    evmId: number;
};
type CosmosChain = {
    type: "cosmos";
};
type StarknetChain = {
    type: "starknet";
};
type RadixChain = {
    type: "radix";
    id: number;
};
type ZetrixChain = {
    type: "zetrix";
};

export type { AptosChain, Chain, ChromiaChain, CosmosChain, EvmChain, FuelChain, NativeCurrency, RadixChain, SolanaChain, StarknetChain, SuiChain, ZetrixChain, ZilliqaChain };
